/**
 * Address - Sync and UI Customization Script
 * -------------------------------------------
 * Purpose:
 * This script is executed on the Address Doctype form.
 * It performs two main functions:
 * 
 * 1. Automatically copies the value from the custom field `custom_jos_ecua_cities`
 *    to the standard ERPNext `city` field. This ensures that custom input is
 *    synced with the system field used in reports, filters, etc.
 * 
 * 2. Hides the `city` field in the form to prevent user confusion or double input.
 *    This is done at the UI level using `frm.set_df_property` so it does not affect the
 *    underlying metadata or database schema (unlike Property Setter).
 */

frappe.ui.form.on('Address', {
    onload: function(frm) {
        // If the standard city field is empty but the custom field has a value,
        // auto-fill it. This happens when loading a new or unsaved form.
        if (!frm.doc.city && frm.doc.custom_jos_ecua_cities) {
            frm.set_value('city', frm.doc.custom_jos_ecua_cities);
        }

        // Hide the standard city field in the UI.
        // This avoids user confusion between native and custom fields.
        // It does NOT create a permanent Property Setter â€” only affects UI.
        frm.set_df_property("city", "hidden", 1);
    },

    validate: function(frm) {
        // Ensure the sync between custom and native field is enforced on save.
        // Even if the user never touched the field manually, the value is copied over.
        if (frm.doc.custom_jos_ecua_cities && frm.doc.city !== frm.doc.custom_jos_ecua_cities) {
            frm.set_value('city', frm.doc.custom_jos_ecua_cities);
        }
    }
});

frappe.ui.form.on('Company', {
    onload(frm) {
        if (!frm.custom_firma_btn_rendered) {
            frm.fields_dict.custom_jos_firma_electronica.$wrapper.append(`
                <div style="margin-top: 10px; margin-bottom: 20px;">
                    <button type="button" class="btn btn-primary" id="btn-validar-firma">
                        âœ… Validar Firma y Transmitir XML a SRI
                    </button>
                </div>
            `);

            frm.fields_dict.custom_jos_firma_electronica.$wrapper.find('#btn-validar-firma').on('click', function () {
                frappe.prompt([
                    {
                        fieldname: 'password',
                        label: 'Ingrese la contraseÃ±a del archivo .p12',
                        fieldtype: 'Password',
                        reqd: 1
                    }
                ], (values) => {
                    const password = values.password;
                    if (!password || password.trim() === "") {
                        frappe.msgprint("âš ï¸ Debes ingresar la contraseÃ±a.");
                        return;
                    }
                    frappe.call({
                        method: "josfe.sri_invoicing.validations.p12_to_pem.convertir_y_validar",
                        args: {
                            company_name: frm.doc.name,
                            p12_password: password
                        },
                        callback: function (res) {
                            if (res.message && res.message.status === "success") {
                                frm.set_value("custom_jos_ruc_certificado", res.message.ruc);
                                frm.set_value("custom_jos_fecha_expiracion", res.message.fecha_expiracion);

                                frm.fields_dict.custom_jos_ruc_certificado.$wrapper.find("input").css("background-color", "#e6ffe6");
                                frm.fields_dict.custom_jos_fecha_expiracion.$wrapper.find("input").css("background-color", "#e6ffe6");

                                frappe.call({
                                    method: "josfe.sri_invoicing.transmission.transmitir_sri.transmitir_xml",
                                    args: {
                                        company_name: frm.doc.name
                                    },
                                    callback: function (r2) {
                                        frappe.msgprint({
                                            title: "ðŸ“¤ TransmisiÃ³n Exitosa",
                                            message: "âœ… Proceso completado. XML transmitido.",
                                            indicator: "green"
                                        });
                                    }
                                });

                            } else {
                                frappe.msgprint({
                                    title: "âŒ Error",
                                    message: res.message.msg || "Error durante la validaciÃ³n.",
                                    indicator: "red"
                                });
                            }
                        }
                    });

                }, 'ValidaciÃ³n de Firma ElectrÃ³nica');
            });

            frm.custom_firma_btn_rendered = true;
        }
    }
});

frappe.ui.form.on('Jos_Establecimientos', {
    jos_matriz: function(frm, cdt, cdn) {
        const selected_row = locals[cdt][cdn];

        // Count how many other rows are already checked
        const checked_others = (frm.doc.custom_jos_tabla_estab || []).filter(
            row => row.jos_matriz && row.name !== selected_row.name
        );

        // If another row is already checked and this one is trying to be checked too
        if (checked_others.length > 0 && selected_row.jos_matriz) {
            frappe.msgprint({
                title: "Solo una matriz permitida",
                message: "Ya hay un establecimiento marcado como matriz. Solo se permite uno.",
                indicator: "red"
            });

            // Cancel the current checkbox toggle
            selected_row.jos_matriz = 0;
            frm.refresh_field('custom_jos_tabla_estab');
            return;
        }

        // If this row is being checked, update direcciÃ³n matriz
        if (selected_row.jos_matriz) {
            frm.set_value('custom_jos_direccion_matriz', selected_row.jos_address || '');
        } else {
            // If unchecked, update based on any other checked row
            const still_checked = (frm.doc.custom_jos_tabla_estab || []).find(row => row.jos_matriz);
            frm.set_value('custom_jos_direccion_matriz', still_checked?.jos_address || '');
        }
    }
});

frappe.ui.form.on('Company', {
  onload: function(frm) {
    // 1. Inject naming series options into the child table field
    frappe.call({
      method: "josfe.api.naming_series.get_naming_series_options_for",
      args: { doctype: "Sales Invoice" },
      callback: function(res) {
        if (res.message) {
          const series_options = res.message.map(r => r.name);

          frm.fields_dict.custom_jos_tabla_estab.grid.update_docfield_property(
            "jos_naming_series",
            "options",
            series_options.join('\n')
          );

          frm.refresh_field("custom_jos_tabla_estab");
        }
      }
    });

    // 2. (Optional) Limit jos_warehouse to group warehouses under a parent
    frm.fields_dict.custom_jos_tabla_estab.grid.get_field('jos_warehouse').get_query = function(doc, cdt, cdn) {
      return {
        filters: {
          parent_warehouse: "All Warehouses - A",
          is_group: 1
        }
      };
    };
  }
});

frappe.ui.form.on('Jos_Establecimientos', {
  jos_warehouse: function(frm, cdt, cdn) {
    const row = locals[cdt][cdn];

    if (row.jos_warehouse) {
      frappe.call({
        method: "josfe.api.naming_series.get_address_for_warehouse",
        args: {
          warehouse: row.jos_warehouse
        },
        callback: function(res) {
          if (res.message) {
            frappe.model.set_value(cdt, cdn, "jos_address", res.message);
          }
        }
      });
    }
  }
});

