frappe.ui.form.on('Contact', {
  onload(frm) {
    maybe_lock_main_contact(frm);
  },
  refresh(frm) {
    maybe_lock_main_contact(frm);
  }
});

function maybe_lock_main_contact(frm) {
  const first_name = (frm.doc.first_name || '').trim();

  const is_main_contact = /^Main Contact (Clte|Prov)-.+/.test(first_name);
  if (!is_main_contact) {
    frm.__main_contact_warning_inserted = false;
    frm.$wrapper.find('.main-contact-warning').remove();
    return;
  }

  // Insert warning only once
  if (!frm.__main_contact_warning_inserted && frm.fields_dict.first_name) {
    const msg_html = `
      <div class="alert alert-danger main-contact-warning" style="margin-bottom: 15px;">
        <strong>âš  AtenciÃ³n:</strong> Este es un Contacto Principal. Si desea editar hÃ¡galo desde la pestaÃ±a <strong>'Entrada RÃ¡pida'</strong> del Cliente o Proveedor.
      </div>`;
    const $msg = $('<div></div>').html(msg_html);
    frm.fields_dict.first_name.$wrapper.before($msg);
    frm.__main_contact_warning_inserted = true;
  }

  // Make all fields read-only
  frm.fields.forEach(field => {
    frm.set_df_property(field.df.fieldname, 'read_only', 1);
  });

  // Disable phone and email tables specifically
  frm.set_df_property('phone_nos', 'read_only', 1);
  frm.set_df_property('email_ids', 'read_only', 1);
}

frappe.ui.form.on('Address', {
  onload(frm) {
