function maybe_lock_main_address(frm) {
  const address_title = (frm.doc.address_title || '').trim();

  const is_main_address = /^Main Dir\. para (Clte|Prov)-.+/.test(address_title);
  if (!is_main_address) {
    frm.__main_address_warning_inserted = false;
    frm.$wrapper.find('.main-address-warning').remove();
    return;
  }

  // Show warning once
  if (!frm.__main_address_warning_inserted && frm.fields_dict.address_title) {
    const msg_html = `
      <div class="alert alert-danger main-address-warning" style="margin-bottom: 15px;">
        <strong>âš  AtenciÃ³n:</strong> Esta es una DirecciÃ³n Principal. Si desea editar hÃ¡galo desde la pestaÃ±a <strong>'Entrada RÃ¡pida'</strong> del Cliente o Proveedor.
      </div>`;
    const $msg = $('<div></div>').html(msg_html);
    frm.fields_dict.address_title.$wrapper.before($msg);
    frm.__main_address_warning_inserted = true;
  }

  // Lock all fields
  frm.fields.forEach(field => {
    frm.set_df_property(field.df.fieldname, 'read_only', 1);
  });
}

// SRI Endpoint â€” efficient list highlighter (class-only, diffed, zero thrash)
frappe.listview_settings["SRI Endpoint"] = {
  onload(listview) {
    const DEBUG = false;
    const LOG = (...a) => DEBUG && console.log("[SRI-LIST]", ...a);

    // 1) Ensure fields arrive in list rows
    listview.settings.add_fields = ["name","active","jos_activo","enabled","is_active","activo","status"];

    // 2) Inject CSS once: stripe via ::before (no DOM nodes), classes per state
    const CSS_ID = "sri-stripe-css";
    if (!document.getElementById(CSS_ID)) {
      const css = document.createElement("style");
      css.id = CSS_ID;
      css.textContent = `
        .sri-row { position: relative; }
        .sri-row::before { content:""; position:absolute; left:0; top:0; bottom:0; width:6px; background:transparent; }
        .sri-active::before   { background:#22c55e; } /* green */
        .sri-inactive::before { background:#ef4444; } /* red   */
        .sri-unknown::before  { background:#9ca3af; } /* gray  */
      `;
      document.head.appendChild(css);
    }

    // 3) State normalizer
    const truthy = v => v === true || v === 1 || v === "1" || v === "true";
    const falsy  = v => v === false || v === 0 || v === "0" || v === "false";
    const stateOf = (doc) => {
      const boolish = (doc.jos_activo ?? doc.enabled ?? doc.is_active ?? doc.activo ?? doc.active);
      if (truthy(boolish)) return "active";
      if (falsy(boolish))  return "inactive";
      const s = String(doc.status || "").toLowerCase();
      if (["active","activo","enabled"].includes(s))   return "active";
      if (["inactive","inactivo","disabled"].includes(s)) return "inactive";
      return "unknown";
    };

    // 4) Row selector detection (v15 themes vary)
    let ROW_SEL = ".list-row";
    const detectRowSelector = () => {
      const $r = listview.$result?.find?.(ROW_SEL);
      if ($r && $r.length) return ROW_SEL;
      ROW_SEL = ".list-row-container";
      return ROW_SEL;
    };

    // 5) One-pass painter, diffed by dataset
    const paintAll = () => {
      const data = listview.data || [];
      const root = listview.$result?.get?.(0);
      if (!root || !data.length) { LOG("no root or no data"); return; }

      const sel = detectRowSelector();
      const rows = root.querySelectorAll(sel);
      const n = Math.min(rows.length, data.length);

      for (let i = 0; i < n; i++) {
        const row = rows[i];
        const doc = data[i];
        // Normalize to a consistent element for class toggling
        const el = row.classList.contains("list-row") ? row : (row.querySelector(".list-row") || row);

        // Mark once
        if (!el.classList.contains("sri-row")) el.classList.add("sri-row");

        // Compute + diff
        const newState = stateOf(doc); // 'active'|'inactive'|'unknown'
        if (el.dataset.sriState === newState) continue; // unchanged â†’ skip

        // Update classes atomically
        el.dataset.sriState = newState;
        el.classList.remove("sri-active","sri-inactive","sri-unknown");
        el.classList.add(`sri-${newState}`);
      }

      // Optional: tidy leftovers if row count shrank
      for (let i = n; i < rows.length; i++) {
        const el = rows[i];
        el.classList?.remove("sri-row","sri-active","sri-inactive","sri-unknown");
        if (el.dataset) delete el.dataset.sriState;
      }
    };

    // 6) Hook exactly once per render (no MutationObserver, no repeated timeouts)
    const origRender = listview.render;
    listview.render = function () {
      const ret = origRender.apply(this, arguments);
      // Paint on next frame to ensure rows are in the DOM
      requestAnimationFrame(paintAll);
      return ret;
    };

    // First pass (initial data may already exist)
    requestAnimationFrame(paintAll);
  },

  // Keep ERPNext's pill (fast + native)
  get_indicator(doc) {
    const truthy = v => v === true || v === 1 || v === "1" || v === "true";
    const falsy  = v => v === false || v === 0 || v === "0" || v === "false";
    const b = (doc.jos_activo ?? doc.enabled ?? doc.is_active ?? doc.activo ?? doc.active);
    let state = "unknown";
    if (truthy(b)) state = "active";
    else if (falsy(b)) state = "inactive";
    else {
      const s = String(doc.status || "").toLowerCase();
      if (["active","activo","enabled"].includes(s)) state = "active";
      else if (["inactive","inactivo","disabled"].includes(s)) state = "inactive";
    }
    if (state === "active")   return ["Activo",   "green", ["active","=",1]];
    if (state === "inactive") return ["Inactivo", "red",   ["active","=",0]];
    return ["Desconocido", "gray"];
  }
};

frappe.ui.form.on('Warehouse', {
  onload_post_render(frm) {
    // Limit length + digits for the parent field
    const f = frm.get_field('custom_establishment_code');
    if (f && f.$input) {
      f.$input.attr('maxlength', 3);
      f.$input.on('input', function () {
        this.value = this.value.replace(/\D/g, '').slice(0, 3);
      });
    }
    limit_child_inputs(frm);
  },
  refresh(frm) {
    limit_child_inputs(frm);
  }
});

function limit_child_inputs(frm) {
  const grid = frm.fields_dict.custom_sri_puntos_emision?.grid;
  if (!grid) return;

  // Re-bind on every refresh to catch new rows
  const $w = $(grid.wrapper);

  // Set maxlength and digit-only behavior for emission point
  $w.find('input[data-fieldname="emission_point_code"]').attr('maxlength', 3);

  $w.off('input.sri-limit').on('input.sri-limit', 'input[data-fieldname="emission_point_code"]', function () {
    this.value = this.value.replace(/\D/g, '').slice(0, 3);
  });
}
